const fetchCoordinatesFromAddress = async (endereco) => {
  try {
    const response = await fetch(
      `https://nominatim.openstreetmap.org/search?q=${encodeURIComponent(endereco)}&format=json&limit=1`
    );
    const data = await response.json();
    if (data && data[0]) {
      return {
        latitude: parseFloat(data[0].lat),
        longitude: parseFloat(data[0].lon)
      };
    } else {
      throw new Error('Endereço não encontrado');
    }
  } catch (error) {
    console.error('Erro ao converter endereço em coordenadas:', error);
    return null;
  }
};

const calcularDistancia = (pontoOrigem, pontoDestino) => {
  // Verifique se os pontos são válidos
  if (
    !pontoOrigem ||
    !pontoDestino ||
    isNaN(pontoOrigem.latitude) ||
    isNaN(pontoOrigem.longitude) ||
    isNaN(pontoDestino.latitude) ||
    isNaN(pontoDestino.longitude)
  ) {
    console.error('Erro: pontos inválidos ou NaN');
    return 0;
  }

  // Fórmula de Haversine para calcular a distância entre dois pontos
  const φ1 = pontoOrigem.latitude * (Math.PI / 180); // Convertendo para radianos
  const φ2 = pontoDestino.latitude * (Math.PI / 180);
  const Δφ = (pontoDestino.latitude - pontoOrigem.latitude) * (Math.PI / 180);
  const Δλ = (pontoDestino.longitude - pontoOrigem.longitude) * (Math.PI / 180);

  const a =
    Math.sin(Δφ / 2) * Math.sin(Δφ / 2) +
    Math.cos(φ1) * Math.cos(φ2) * Math.sin(Δλ / 2) * Math.sin(Δλ / 2);
  const c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1 - a));

  const raioTerra = 6371; // Raio da Terra em km
  const distancia = raioTerra * c; // Distância em km

  return distancia;
};

const calcularDistanciaEntreEnderecos = async (enderecoOrigem, enderecoDestino) => {
  // Obtenha as coordenadas para os dois endereços
  const pontoOrigem = await fetchCoordinatesFromAddress(enderecoOrigem);
  const pontoDestino = await fetchCoordinatesFromAddress(enderecoDestino);

  if (!pontoOrigem || !pontoDestino) {
    console.error('Erro: não foi possível obter coordenadas');
    return;
  }

