      <ul>
        {solicitacoes.map(s => {
            console.log(s);
          const distancia = calcularDistancia(s.origem, s.destino);
          const valor = calcularValor(distancia);
          return (
            <li key={s.id}>
              <h3>Solicitação de {s.cliente}</h3>
              <p>Origem: {s.origem.endereco} - Destino: {s.destino.endereco}</p>
              <p>Distância: {distancia} km</p>
              <p>Valor: R${valor}</p>
            </li>
          );
        })}
      </ul>
    </div>
  );
}

