import React, { useState } from 'react';
import api from './Api';
import { useNavigate } from 'react-router-dom';

function NovaSolicitacao() {
  const [origem, setOrigem] = useState('');
  const [destino, setDestino] = useState('');
  const [data, setData] = useState('');
  const [hora, setHora] = useState('');
  const [descricao, setDescricao] = useState('');
  const navigate = useNavigate();
  const [menuAberto, setMenuAberto] = useState(false);

  const subMenu = [
    { name: 'Home', path: '/' },
    { name: 'Categories', path: '/Categories' },
    { name: 'Products', path: '/Products' },
    { name: 'About Us', path: '/Contact' },
  ];

  const toggleMenu = () => {
    setMenuAberto(!menuAberto);
  };

  const fecharMenu = () => {
    setMenuAberto(false);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    api.post('solicitacoes/', {
      origem,
      destino,
      data,
      hora,
      descricao,
    })
    .then(response => {
      navigate('/solicitacoes');
    })
    .catch(error => {
      console.error("Erro ao criar solicitação:", error);
    });
  };

  return (
    <div>
    <div className='menunav'>
      <button className="menu-toggle" onClick={toggleMenu}>☰</button>
      <nav className={`navbar ${menuAberto ? 'ativo' : ''}`}>
        <ul className="lista">
          {subMenu.map((x) => (<li className='itens' key={x.name}>
            <Link to={x.path} className='menu-link' onClick={fecharMenu}>{x.name}</Link>
          </li>))}
        </ul>
      </nav>
    </div>
      <h2>Nova Solicitação de Serviço</h2>
      <form onSubmit={handleSubmit}>
        <label>
          Origem:
          <input type="text" value={origem} onChange={e => setOrigem(e.target.value)} required />
        </label>
        <br />
        <label>
          Destino:
          <input type="text" value={destino} onChange={e => setDestino(e.target.value)} required />
        </label>
        <br />
        <label>
          Data:
          <input type="date" value={data} onChange={e => setData(e.target.value)} required />
        </label>
        <br />
        <label>
          Hora:
          <input type="time" value={hora} onChange={e => setHora(e.target.value)} required />
        </label>
        <br />
        <label>
          Descrição:
          <textarea value={descricao} onChange={e => setDescricao(e.target.value)} />
        </label>
        <br />
        <button type="submit">Criar Solicitação</button>
      </form>
    </div>
  );
}

export default NovaSolicitacao;
