import React, { useEffect, useState } from 'react';
import api from './Api';
import L from 'leaflet';  // Importando o Leaflet para o cálculo da distância

function Solicitacoes() {
  const [solicitacoes, setSolicitacoes] = useState([]);

  useEffect(() => {
    api.get('solicitacoes/')
      .then(response => {
        setSolicitacoes(response.data);
      })
      .catch(error => {
        console.error("Erro ao buscar solicitações:", error);
      });
  }, []);

  // Função para calcular a distância entre dois pontos
  const calcularDistancia = (origem, destino) => {
    const pontoOrigem = L.latLng(origem.latitude, origem.longitude);
    const pontoDestino = L.latLng(destino.latitude, destino.longitude);
    const distanciaMetros = pontoOrigem.distanceTo(pontoDestino); // Distância em metros
    const distanciaKm = (distanciaMetros / 1000).toFixed(2); // Convertendo para quilômetros
    return distanciaKm;
  };

  // Função para calcular o valor com base na distância
  const calcularValor = (distancia) => {
    return (distancia * 1.5).toFixed(2);  // Multiplica a distância por 1,5
  };

  return (
    <div>
      <h2>Solicitações de Serviço</h2>
      <ul>
        {solicitacoes.map(s => {
          const distancia = calcularDistancia(s.origem, s.destino);
          const valor = calcularValor(distancia);
          return (
            <li key={s.id}>
              <h3>Solicitação de {s.cliente}</h3>
              <p>Origem: {s.origem.endereco} - Destino: {s.destino.endereco}</p>
              <p>Distância: {distancia} km</p>
              <p>Valor: R${valor}</p>
            </li>
          );
        })}
      </ul>
    </div>
  );
}

export default Solicitacoes;
