import React, { useEffect, useState } from "react";

const Motorista = () => {
    const [solicitacoes, setSolicitacoes] = useState([]);
    const [detalheSolicitacao, setDetalheSolicitacao] = useState(null);
    
    useEffect(() => {
        fetch("http://192.168.0.237:8000/api/solicitacoes/", {
            headers: {
                Authorization: `Bearer ${localStorage.getItem("token")}`,
            },
        })
        .then(response => response.json())
        .then(data => setSolicitacoes(data))
        .catch(error => console.error("Erro ao buscar solicitações:", error));
    }, []);

    const aceitarSolicitacao = (id) => {
        console.log(`Corrida ${id} aceita`);
        alert("Corrida aceita com sucesso!");
    };

    const recusarSolicitacao = (id) => {
        console.log(`Corrida ${id} recusada`);
        alert("Corrida recusada!");
    };

    return (
        <div>
            <h1>Painel do Motorista</h1>
            <div>
                {solicitacoes.map(solicitacao => (
                    <div key={solicitacao.id} className="card">
                        <h3>Solicitação de {solicitacao.cliente}</h3>
                        <p>Origem: {solicitacao.origem}</p>
                        <p>Destino: {solicitacao.destino}</p>
                        <button onClick={() => setDetalheSolicitacao(solicitacao)}>Ver detalhes</button>
                    </div>
                ))}
            </div>

            {detalheSolicitacao && (
                <div className="modal">
                    <h2>Detalhes da Solicitação</h2>
                    <p>Cliente: {detalheSolicitacao.cliente}</p>
                    <p>Origem: {detalheSolicitacao.origem}</p>
                    <p>Destino: {detalheSolicitacao.destino}</p>
                    <p>Data: {detalheSolicitacao.data}</p>
                    <p>Hora: {detalheSolicitacao.hora}</p>
                    <p>Descrição: {detalheSolicitacao.descricao}</p>
                    <button onClick={() => aceitarSolicitacao(detalheSolicitacao.id)}>Aceitar Corrida</button>
                    <button onClick={() => recusarSolicitacao(detalheSolicitacao.id)}>Recusar Corrida</button>
                    <button onClick={() => setDetalheSolicitacao(null)}>Fechar</button>
                </div>
            )}
        </div>
    );
};

export default Motorista;
