import { useEffect, useState } from "react";
import axios from "axios";
import './motorista.css'

const Motorista = () => {
  const [solicitacoes, setSolicitacoes] = useState([]); // Lista de solicitações
  const [modalOpen, setModalOpen] = useState(false); // Controle do modal
  const [solicitacaoSelecionada, setSolicitacaoSelecionada] = useState(null); // Armazena a solicitação clicada

  const accessToken = localStorage.getItem("access_token");

  useEffect(() => {
    axios
      .get("http://192.168.0.237:8000/api/solicitacoes/", {
        headers: { Authorization: `Bearer ${accessToken}` },
      })
      .then((response) => {
        console.log("Resposta da API:", response.data);
        setSolicitacoes(Array.isArray(response.data) ? response.data : response.data.results || []);
      })
      .catch((error) => {
        console.error("Erro ao buscar solicitações:", error);
      });
  }, []);

  const calcularDistancia = (lat1, lng1, lat2, lng2) => {
    if (!lat1 || !lng1 || !lat2 || !lng2) return 0;
    const R = 6371; // Raio da Terra em km
    const dLat = (lat2 - lat1) * (Math.PI / 180);
    const dLng = (lng2 - lng1) * (Math.PI / 180);
    const a =
      Math.sin(dLat / 2) * Math.sin(dLat / 2) +
      Math.cos(lat1 * (Math.PI / 180)) *
        Math.cos(lat2 * (Math.PI / 180)) *
        Math.sin(dLng / 2) *
        Math.sin(dLng / 2);
    const c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1 - a));
    return (R * c).toFixed(2); // Retorna a distância formatada
  };

  const abrirModal = (solicitacao) => {
    setSolicitacaoSelecionada(solicitacao);
    setModalOpen(true);
  };

  const fecharModal = () => {
    setModalOpen(false);
    setSolicitacaoSelecionada(null);
  };

  return (
    <div className="container">
      <h1>Solicitações de Corrida</h1>
      {solicitacoes.length === 0 ? (
        <p>Nenhuma solicitação disponível.</p>
      ) : (
        <div className="grid">
          {solicitacoes.map((solicitacao) => {
            const distancia = calcularDistancia(
              parseFloat(solicitacao.origem_lat),
              parseFloat(solicitacao.origem_lng),
              parseFloat(solicitacao.destino_lat),
              parseFloat(solicitacao.destino_lng)
            );
            const valor = (distancia * 1.5).toFixed(2);

            return (
              <div key={solicitacao.id} className="card" onClick={() => abrirModal(solicitacao)}>
                <h3>{solicitacao.cliente_nome}</h3>
                <p>Distância: {distancia} km</p>
                <p>Valor estimado: R$ {valor}</p>
              </div>
            );
          })}
        </div>
      )}

      {modalOpen && solicitacaoSelecionada && (
        <div className="modal">
          <div className="modal-content">
            <h2>Detalhes da Solicitação</h2>
            <p><strong>Cliente:</strong> {solicitacaoSelecionada.cliente_nome}</p>
            <p><strong>Distância:</strong> {calcularDistancia(
              parseFloat(solicitacaoSelecionada.origem_lat),
              parseFloat(solicitacaoSelecionada.origem_lng),
              parseFloat(solicitacaoSelecionada.destino_lat),
              parseFloat(solicitacaoSelecionada.destino_lng))} km</p>
            <p><strong>Valor Estimado:</strong> R$ {(calcularDistancia(
              parseFloat(solicitacaoSelecionada.origem_lat),
              parseFloat(solicitacaoSelecionada.origem_lng),
              parseFloat(solicitacaoSelecionada.destino_lat),
              parseFloat(solicitacaoSelecionada.destino_lng)) * 1.5).toFixed(2)}</p>
            <p><strong>Origem:</strong> {solicitacaoSelecionada.origem_endereco}</p>
            <p><strong>Destino:</strong> {solicitacaoSelecionada.destino_endereco}</p>
            <button className="accept">Aceitar</button>
            <button className="reject">Recusar</button>
            <button className="close" onClick={fecharModal}>Fechar</button>
          </div>
        </div>
      )}
    </div>
  );
};

export default Motorista;
