import { useEffect, useState } from "react";
import axios from "axios";

const Motorista = () => {
  const [solicitacoes, setSolicitacoes] = useState([]); // Lista de solicitações
  const [modalOpen, setModalOpen] = useState(false); // Controle do modal
  const [solicitacaoSelecionada, setSolicitacaoSelecionada] = useState(null); // Armazena a solicitação clicada

  const accessToken = localStorage.getItem("access_token");

  useEffect(() => {
    axios
      .get("http://192.168.0.237:8000/api/solicitacoes/", {
        headers: { Authorization: `Bearer ${accessToken}` },
      })
      .then((response) => {
        console.log("Resposta da API:", response.data);
        setSolicitacoes(response.data.results || []); // Se paginado, pega `results`
      })
      .catch((error) => {
        console.error("Erro ao buscar solicitações:", error);
      });
  }, []);

  const abrirModal = (solicitacao) => {
    setSolicitacaoSelecionada(solicitacao);
    setModalOpen(true);
  };

  const fecharModal = () => {
    setModalOpen(false);
    setSolicitacaoSelecionada(null);
  };

  return (
    <div className="container">
      <h1>Solicitações de Corrida</h1>
      {solicitacoes.length === 0 ? (
        <p>Nenhuma solicitação disponível.</p>
      ) : (
        <div className="grid">
          {solicitacoes.map((solicitacao) => (
            <div key={solicitacao.id} className="card" onClick={() => abrirModal(solicitacao)}>
              <h3>{solicitacao.cliente_nome}</h3>
              <p>Clique para mais detalhes</p>
            </div>
          ))}
        </div>
      )}

      {modalOpen && solicitacaoSelecionada && (
        <div className="modal">
          <div className="modal-content">
            <h2>Detalhes da Solicitação</h2>
            <p><strong>Cliente:</strong> {solicitacaoSelecionada.cliente_nome}</p>
            <p><strong>Detalhes:</strong> {solicitacaoSelecionada.detalhes}</p>
            <button className="accept">Aceitar</button>
            <button className="reject">Recusar</button>
            <button className="close" onClick={fecharModal}>Fechar</button>
          </div>
        </div>
      )}

      <style jsx>{`
        .container {
          text-align: center;
          padding: 20px;
        }
        .grid {
          display: flex;
          flex-wrap: wrap;
          gap: 10px;
          justify-content: center;
        }
        .card {
          background: #f8f9fa;
          padding: 20px;
          border-radius: 10px;
          cursor: pointer;
          box-shadow: 2px 2px 10px rgba(0, 0, 0, 0.1);
        }
        .modal {
          position: fixed;
          top: 0;
          left: 0;
          width: 100%;
          height: 100%;
          background: rgba(0, 0, 0, 0.5);
          display: flex;
          justify-content: center;
          align-items: center;
        }
        .modal-content {
          background: white;
          padding: 20px;
          border-radius: 10px;
          text-align: center;
        }
        .accept, .reject, .close {
          margin: 10px;
          padding: 10px;
          cursor: pointer;
        }
        .accept { background: green; color: white; }
        .reject { background: red; color: white; }
        .close { background: gray; color: white; }
      `}</style>
    </div>
  );
};

export default Motorista;
