import { useEffect, useState } from "react";
import axios from "axios";

const Motorista = () => {
  const [solicitacoes, setSolicitacoes] = useState([]); // üîπ Garante que o estado inicial √© um array
  const accessToken = localStorage.getItem("access_token");

  useEffect(() => {
    axios
      .get("http://192.168.0.237:8000/api/solicitacoes/", {
        headers: { Authorization: `Bearer ${accessToken}` },
      })
      .then((response) => {
        console.log("Resposta da API:", response.data); // üîπ Debug para ver o formato da resposta
        if (Array.isArray(response.data)) {
          setSolicitacoes(response.data);
        } else {
          setSolicitacoes([]); // üîπ Evita erro se a resposta n√£o for uma lista
        }
      })
      .catch((error) => {
        console.error("Erro ao buscar solicita√ß√µes:", error);
      });
  }, []);

  return (
    <div>
      <h1>Solicita√ß√µes de Corrida</h1>
      {solicitacoes.length === 0 ? (
        <p>Nenhuma solicita√ß√£o dispon√≠vel.</p>
      ) : (
        solicitacoes.map((solicitacao) => (
          <div key={solicitacao.id} className="card">
            <h3>{solicitacao.cliente}</h3>
            <p>{solicitacao.detalhes}</p>
            <button>Aceitar</button>
            <button>Recusar</button>
          </div>
        ))
      )}
    </div>
  );
};

export default Motorista;
