import React, { useEffect, useState } from 'react';
import { MapContainer, TileLayer, Marker, useMapEvents } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';
import L from 'leaflet';
import api from './Api';
import { useNavigate } from 'react-router-dom';

// Configuração dos ícones do Leaflet
delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
  iconRetinaUrl: require('leaflet/dist/images/marker-icon-2x.png'),
  iconUrl: require('leaflet/dist/images/marker-icon.png'),
  shadowUrl: require('leaflet/dist/images/marker-shadow.png'),
});

// Função para converter coordenadas em endereço (reverse geocoding) via API Nominatim
const fetchAddressFromCoordinates = async (lat, lng) => {
  try {
    const response = await fetch(
      `https://nominatim.openstreetmap.org/reverse?lat=${lat}&lon=${lng}&format=json`
    );
    const data = await response.json();
    return data.display_name; // Endereço formatado
  } catch (error) {
    console.error('Erro ao converter coordenadas em endereço:', error);
    return '';
  }
};

// Componente que captura cliques no mapa para selecionar localização
const MapSelector = ({ selectMode, onLocationSelected }) => {
  useMapEvents({
    click: async (e) => {
      const address = await fetchAddressFromCoordinates(e.latlng.lat, e.latlng.lng);
      onLocationSelected(e.latlng, address, selectMode);
    },
  });
  return null;
};

function NovaSolicitacao() {
  // Estados para os campos originais
  const [data, setData] = useState('');
  const [hora, setHora] = useState('');
  const [descricao, setDescricao] = useState('');
  
  // Estados para origem e destino (endereços)
  const [origem, setOrigem] = useState('');
  const [destino, setDestino] = useState('');
  
  // Estados para armazenar as coordenadas (usados para posicionar os marcadores)
  const [markerOrigin, setMarkerOrigin] = useState(null);
  const [markerDestination, setMarkerDestination] = useState(null);
  
  // Estado para a posição atual do usuário (centro do mapa)
  const [currentPosition, setCurrentPosition] = useState([ -23.55052, -46.633308 ]); // valor padrão
  const [selectMode, setSelectMode] = useState('origin'); // 'origin' ou 'destination'
  
  const navigate = useNavigate();

  // Obter a localização atual do usuário
  useEffect(() => {
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition((position) => {
        setCurrentPosition([
          position.coords.latitude,
          position.coords.longitude,
        ]);
      });
    }
  }, []);

  // Atualiza origem ou destino quando o usuário seleciona no mapa (via clique)
  const handleLocationSelected = (latlng, address, mode) => {
    if (mode === 'origin') {
      setMarkerOrigin(latlng);
      setOrigem(address);
    } else {
      setMarkerDestination(latlng);
      setDestino(address);
    }
  };

  // Permite que o usuário ajuste a posição arrastando o marcador
  const handleDragEnd = async (e, mode) => {
    const marker = e.target;
    const position = marker.getLatLng();
    const address = await fetchAddressFromCoordinates(position.lat, position.lng);
    if (mode === 'origin') {
      setMarkerOrigin(position);
      setOrigem(address);
    } else {
      setMarkerDestination(position);
      setDestino(address);
    }
  };

  // Submete os dados para o backend – mantendo os dados originais e os endereços selecionados
  const handleSubmit = (e) => {
    e.preventDefault();
    api.post('solicitacoes/', {
      origem,    // endereço obtido do mapa
      destino,   // endereço obtido do mapa
      data,
      hora,
      descricao,
    })
    .then(response => {
      navigate('/solicitacoes');
    })
    .catch(error => {
      console.error("Erro ao criar solicitação:", error);
    });
  };

  return (
    <div style={{ position: 'relative', height: '100vh' }}>
      {/* Mapa como plano de fundo */}
      <MapContainer center={currentPosition} zoom={13} style={{ height: '100%', width: '100%' }}>
        <TileLayer
          attribution='&copy; OpenStreetMap contributors'
          url='https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png'
        />
        {/* Componente que captura cliques no mapa */}
        <MapSelector selectMode={selectMode} onLocationSelected={handleLocationSelected} />
        {/* Marcador de Origem */}
        {markerOrigin && (
          <Marker
            position={markerOrigin}
            draggable={true}
            eventHandlers={{
              dragend: (e) => handleDragEnd(e, 'origin'),
            }}
          />
        )}
        {/* Marcador de Destino */}
        {markerDestination && (
          <Marker
            position={markerDestination}
            draggable={true}
            eventHandlers={{
              dragend: (e) => handleDragEnd(e, 'destination'),
            }}
          />
        )}
      </MapContainer>

      {/* Overlay para seleção de Origem e Destino */}
      <div style={{
          position: 'absolute',
          top: '10px',
          left: '50%',
          transform: 'translateX(-50%)',
          background: 'rgba(255,255,255,0.9)',
          padding: '10px',
          borderRadius: '8px',
          zIndex: 1000,
          width: '90%',
          maxWidth: '500px'
      }}>
        <div>
          <label><strong>Origem:</strong></label>
          <input
            type="text"
            value={origem}
            readOnly
            placeholder="Clique no mapa para selecionar a origem"
            style={{ width: '100%', padding: '5px', marginTop: '5px' }}
          />
        </div>
        <div style={{ marginTop: '10px' }}>
          <label><strong>Destino:</strong></label>
          <input
            type="text"
            value={destino}
            readOnly
            placeholder="Clique no mapa para selecionar o destino"
            style={{ width: '100%', padding: '5px', marginTop: '5px' }}
          />
        </div>
        <div style={{ marginTop: '10px', textAlign: 'center' }}>
          <button type="button" onClick={() => setSelectMode('origin')} style={{ marginRight: '10px' }}>
            Selecionar Origem
          </button>
          <button type="button" onClick={() => setSelectMode('destination')}>
            Selecionar Destino
          </button>
        </div>
      </div>

      {/* Formulário para os demais dados (data, hora, descrição) */}
      <div style={{
          position: 'absolute',
          bottom: '20px',
          left: '50%',
          transform: 'translateX(-50%)',
          background: 'rgba(255,255,255,0.9)',
          padding: '10px',
          borderRadius: '8px',
          zIndex: 1000,
          width: '90%',
          maxWidth: '500px'
      }}>
        <h2>Nova Solicitação de Serviço</h2>
        <form onSubmit={handleSubmit}>
          <label>
            Data:
            <input type="date" value={data} onChange={e => setData(e.target.value)} required />
          </label>
          <br />
          <label>
            Hora:
            <input type="time" value={hora} onChange={e => setHora(e.target.value)} required />
          </label>
          <br />
          <label>
            Descrição:
            <textarea value={descricao} onChange={e => setDescricao(e.target.value)} />
          </label>
          <br />
          <button type="submit">Criar Solicitação</button>
        </form>
      </div>
    </div>
  );
}

export default NovaSolicitacao;
