import { useEffect, useState } from "react";
import axios from "axios";
import "./motorista.css";

// Função para calcular distância entre dois pontos em km
const calcularDistancia = (lat1, lon1, lat2, lon2) => {
  if (!lat1 || !lon1 || !lat2 || !lon2) return 0;

  const R = 6371; // Raio da Terra em km
  const dLat = ((lat2 - lat1) * Math.PI) / 180;
  const dLon = ((lon2 - lon1) * Math.PI) / 180;
  const a =
    Math.sin(dLat / 2) * Math.sin(dLat / 2) +
    Math.cos((lat1 * Math.PI) / 180) *
      Math.cos((lat2 * Math.PI) / 180) *
      Math.sin(dLon / 2) *
      Math.sin(dLon / 2);
  const c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1 - a));
  return (R * c).toFixed(2);
};

// Função para obter endereço a partir das coordenadas
const obterEndereco = async (lat, lng) => {
  if (!lat || !lng) return "Endereço não disponível";

  console.log(`Buscando endereço para lat: ${lat}, lng: ${lng}`);

  try {
    await new Promise((resolve) => setTimeout(resolve, 500)); // Atraso para evitar bloqueios da API
    const response = await fetch(
      `https://nominatim.openstreetmap.org/reverse?lat=${lat}&lon=${lng}&format=json`
    );
    const data = await response.json();
    console.log("Resposta OpenStreetMap:", data);

    if (data.error) {
      return "Endereço não encontrado";
    }

    const { road, suburb, city } = data.address || {};
    return [road, suburb, city].filter(Boolean).join(", ") || "Endereço não encontrado";
  } catch (error) {
    console.error("Erro ao obter endereço:", error);
    return "Erro ao obter endereço";
  }
};

const Motorista = () => {
  const [solicitacoes, setSolicitacoes] = useState([]); // Lista de solicitações
  const [modalOpen, setModalOpen] = useState(false); // Controle do modal
  const [solicitacaoSelecionada, setSolicitacaoSelecionada] = useState(null); // Armazena solicitação clicada

  const accessToken = localStorage.getItem("access_token");

  useEffect(() => {
    axios
      .get("http://192.168.0.237:8000/api/solicitacoes/", {
        headers: { Authorization: `Bearer ${accessToken}` },
      })
      .then(async (response) => {
        const dados = Array.isArray(response.data)
          ? response.data
          : response.data.results || [];

        const processadas = await Promise.all(
          dados.map(async (solicitacao) => {
            console.log("Solicitação recebida:", solicitacao);

            const distancia = calcularDistancia(
              solicitacao.origem_lat,
              solicitacao.origem_lng,
              solicitacao.destino_lat,
              solicitacao.destino_lng
            );
            const preco = (distancia * 1.5).toFixed(2);

            const origem = await obterEndereco(solicitacao.origem_lat, solicitacao.origem_lng);
            const destino = await obterEndereco(solicitacao.destino_lat, solicitacao.destino_lng);

            return { ...solicitacao, distancia, preco, origem, destino };
          })
        );

        setSolicitacoes(processadas);
      })
      .catch((error) => {
        console.error("Erro ao buscar solicitações:", error);
      });
  }, []);

  const abrirModal = (solicitacao) => {
    setSolicitacaoSelecionada(solicitacao);
    setModalOpen(true);
  };

  const fecharModal = () => {
    setModalOpen(false);
    setSolicitacaoSelecionada(null);
  };

  return (
    <div className="container">
      <h1>Solicitações de Corrida</h1>
      {solicitacoes.length === 0 ? (
        <p>Nenhuma solicitação disponível.</p>
      ) : (
        <div className="grid">
          {solicitacoes.map((solicitacao) => (
            <div key={solicitacao.id} className="card" onClick={() => abrirModal(solicitacao)}>
              <h3>{solicitacao.cliente_nome}</h3>
              <p><strong>Distância:</strong> {solicitacao.distancia} km</p>
              <p><strong>Valor:</strong> R$ {solicitacao.preco}</p>
            </div>
          ))}
        </div>
      )}

      {modalOpen && solicitacaoSelecionada && (
        <div className="modal">
          <div className="modal-content">
            <h2>Detalhes da Solicitação</h2>
            <p><strong>Cliente:</strong> {solicitacaoSelecionada.cliente_nome}</p>
            <p><strong>Distância:</strong> {solicitacaoSelecionada.distancia} km</p>
            <p><strong>Valor:</strong> R$ {solicitacaoSelecionada.preco}</p>
            <p><strong>Origem:</strong> {solicitacaoSelecionada.origem}</p>
            <p><strong>Destino:</strong> {solicitacaoSelecionada.destino}</p>
            <button className="accept">Aceitar</button>
            <button className="reject">Recusar</button>
            <button className="close" onClick={fecharModal}>Fechar</button>
          </div>
        </div>
      )}
    </div>
  );
};

export default Motorista;
